-- Se cambia FechaCambio por FechaCorresponde para el filtro.
update consultasclientes set Query="select mcd.FechaCambio as FechaDelCambio,mcd.FechaCorresponde as FechaCorrespondiente, cli.Nombre as Cliente,ser.Nombre as Servicio,mcd.NroEmpleado,emp.Nombre,emp.Apellido,tmcd.descripcion,mcd.Observaciones from Clientes cli, Servicios ser, empleados emp, motivoscambiosdiarios mcd, tiposmotivocambiodiario tmcd where DATE(mcd.FechaCorresponde)='FECHASOLA' and emp.NroEmpleado= mcd.NroEmpleado and tmcd.IdTipoMotivo=mcd.IdTipoMotivo and cli.NumeroCliente=mcd.NumeroCliente and ser.NumeroCliente = mcd.NumeroCliente and ser.NumeroServicio = mcd.NumeroServicio" where idConsultaCliente=4  and Nombre="Motivos de Cambios Diarios en los Escalafones";

-- Se agrega FechaIngreso en el Listado de Historial Empleado.
update consultasempleados set Query="select emp.NroEmpleado, emp.Apellido, emp.Nombre, emp.FechaIngreso, teh.Nombre as Tipo, ehe.Descripcion, ehe.FechaInicio, ehe.FechaFin from EventosHistorialEmpleado ehe, Empleados emp, TiposEventoHistorial teh where ehe.IdEmpleado=emp.NroEmpleado and ehe.IdTipoEvento = teh.IdTipoEventoHistorial and (FechaInicio between 'FECHADESDE' and 'FECHAHASTA' or FechaFin between 'FECHADESDE' and 'FECHAHASTA') IFNROEMPLEADO" where idConsultaEmpleado=4  and Nombre="HISTORIAL DE FUNCIONARIOS";

insert into consultasempleados values (0,"TOTAL EXTRASLIQUIDACION SUMARIZADO","Devuelve el valor total sumarizado de los extrasliquidacion que tienen los empleados (O un empleado dado) en un rango de Fecha dado.","select emp.NroEmpleado,emp.Apellido,emp.Nombre,sum(if(el.Signo=0,cel.ValorCuota*(-1),cel.ValorCuota)) as ValorTotalExtras from cuotasextrasliquidacion cel, extrasliquidacion el, empleados emp where el.IdExtraLiquidacion = cel.IdExtraLiquidacion and el.IdEmpleado = emp.NroEmpleado and cel.Fecha between 'FECHADESDE' and 'FECHAHASTA' IFNROEMPLEADO group by emp.NroEmpleado,emp.Apellido,emp.Nombre",1);